mapper.select:
select 

from playerinfo
where id = #{id,jdbcType=INTEGER}
  and qqnumber = #{qqnumber,jdbcType=INTEGER}

mapper.delete:
delete from playerinfo
where id = #{id,jdbcType=INTEGER}
  and qqnumber = #{qqnumber,jdbcType=INTEGER}

mapper.insert:
insert into playerinfo (id, qqnumber, point
  )
values (#{id,jdbcType=INTEGER}, #{qqnumber,jdbcType=INTEGER}, #{point,jdbcType=INTEGER}
  )

mapper.update:
